import React, { Component } from 'react';
import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardActions, Button, CardMenu, IconButton } from 'react-mdl';

class Project extends Component{
    constructor(props){
        super(props);
        this.state = {activeTab: 0};
    }
    toggleCategories(){
        if(this.state.activeTab == 0){
            return(
               <Card shadow={5} style={{minWidh: '450', margin: 'auto'}}>
                <CardTitle style={{color: '#fff', height: '176px',background:'url(https://www.google.com/url?sa=i&source=images&cd=&cad=rja&uact=8&ved=2ahUKEwiA7OmDwOrhAhUEcCsKHdsyDyYQjRx6BAgBEAU&url=https%3A%2F%2Fgithub.com%2Fsilvestrevivo%2Freact-parcel-starter&psig=AOvVaw23TWM-l33TUeNvNfNgRdP_&ust=1556255497145942) center / cover'}}></CardTitle>
               </Card>
            )
        } else if(this.state.activeTab == 1){
            return(
                <div><h1>This is Angular</h1></div>
            )
        } else if (this.state.activeTab == 2){
            return(
                <div><h1>This is VueJS</h1></div>
            )
        }
        else if (this.state.activeTab == 3){
            return(
                <div><h1>This is MongoDB</h1></div>
            )
        }
    }
    render(){
        return(
             <div className="category-tabs">
        <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({activeTab: tabId})} ripple>
           <Tab>React</Tab>
           <Tab>Angular</Tab>
           <Tab>VueJS</Tab>
           <Tab>MongoDB</Tab>
        </Tabs>
        <section className="projects-grid">
            <Grid className="projects-grid">
              <Cell col={12}>
               <div className="content">{this.toggleCategories()}</div>
              </Cell>
            </Grid>
        </section>

             </div>
        )
    }
}

export default Project;
